        <div className="space-y-1 text-sm">
          {gameState.messages.slice(-5).map((message, index) => (
            <div key={index} className={`${message.type === 'damage' ? 'text-red-400' : message.type === 'success' ? 'text-green-400' : 'text-gray-300'}`}>
              {message.text}
            </div>
          ))}
